def replace_with_previous_letter(text):
    result = ""
    for char in text:
        if char.isalpha():
            if char == 'a':
                result += 'z'
            elif char == 'A':
                result += 'Z'
            else:
                result += chr(ord(char) - 1)
        else:
            result += char
    return result


input_text = ""
output_text = replace_with_previous_letter(input_text)
print(output_text)
